import React from "react";
import { Card, Row, Col, Dropdown } from "react-bootstrap";

function ProductCategories({ categories }) {
  return (
    <div className="product-categories my-4">
      <h3 className="section-title">Product Categories</h3>
      <Dropdown className="mb-3">
        <Dropdown.Toggle variant="secondary" id="dropdown-basic">
          Select Category
        </Dropdown.Toggle>
        <Dropdown.Menu>
          {categories.map((category) => (
            <Dropdown.Item key={category.id} href={`#category-${category.id}`}>
              {category.name}
            </Dropdown.Item>
          ))}
        </Dropdown.Menu>
      </Dropdown>
      <Row>
        {categories.map((category) => (
          <Col key={category.id} sm={12} md={6} lg={4} xl={3}>
            <Card className="mb-4 category-card" id={`category-${category.id}`}>
              <Card.Img
                variant="top"
                src={category.image}
                className="category-image"
              />
              <Card.Body>
                <Card.Title>{category.name}</Card.Title>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
      <style jsx>{`
        .section-title {
          font-size: 1.5rem;
          margin-bottom: 1rem;
        }
        .category-card {
          transition: transform 0.2s;
        }
        .category-card:hover {
          transform: scale(1.05);
        }
        .category-image {
          height: 200px;
          object-fit: cover;
        }
      `}</style>
    </div>
  );
}

export default ProductCategories;
